      const email = req.body.email;
      const password = req.body.password;

      User.findOne({ email: email }, function(err, user) {
        if (err) {
          console.log(err);
          return next(err);
        }
        if (user) {
          return res.send({ message: &quot;User Already Exists&quot; });
        }
        const newUser = new User({
          email: email,
          password: password
        });
        newUser.save(next);
      });
    },
    passport.authenticate(&quot;login&quot;)
  );
