            Event::assertDispatched(NewItemCreated::class, function ($e) {
                return $e-&gt;name === &#39;test&#39; ;
            });
    
            // Assert an event was dispatched twice...
            Event::assertDispatched(NewItemCreated::class, 2);
    
            // Assert an event was not dispatched...
