    function highlight() {
      items.forEach(function(item) {
        item.classList.replace(&#39;on&#39;, &#39;off&#39;);
      });
      this.classList.replace(&#39;off&#39;, &#39;on&#39;);
    }

    var items = document.querySelectorAll(&quot;.off&quot;);
    items.forEach(function(item) {
      item.addEventListener(&quot;click&quot;, highlight);
    });

