		cxChar = tm.tmAveCharWidth;
		cxCaps = (tm.tmPitchAndFamily &amp; 1 ? 3 : 2) * cxChar / 2;
		cyChar = tm.tmHeight + tm.tmExternalLeading;

		ReleaseDC(hwnd, hdc);

		iMaxWidth = 40 * cxChar + 22 * cxCaps;
