    export default function snq&lt;T = any&gt;(callback: () =&gt; T, defaultValue?: T) {
      try {
        const result = callback();
        return typeof result === &#39;undefined&#39; ? defaultValue : result;
      } catch (err) {
        if (err instanceof TypeError) {
          return defaultValue;
        }
    
