	&lt;?php
	$current_state = &quot;&quot;;
	$row_data = mysql_query(&quot;select * from author order by author_state&quot; );
	while($row = mysql_fetch_array($row_data) ) {

		// Output the &quot;new&quot; state
		if ($row[&#39;author_state&#39;] != $current_state)
