        layout (local_size_x = COMPUTEPATCHSIZE, local_size_y = COMPUTEPATCHSIZE) in;
        layout(binding=0, rgba8) uniform readonly highp image2D inputImage;
        layout(binding=1, rgba8) uniform writeonly highp image2D resultImage;
        uniform int radius;
        void main()
