    function Table()
    {
        var njson = 0;
        var ntable = 0;
        var nth = 0;
        var ntr = 0;
        var ntd = 0;

        this.init = function ()
        {
            njson = 0;
            ntable = 0;
            nth = 0;
            ntr = 0;
