            ++njson;
        }

        this.create = function ( tag, class_name, to, text )
        {
            if( text === undefined )
            {
                text = &#39;&#39;;
            }

            switch( tag )
            {
                case &#39;TABLE&#39;:
                var table = document.createElement( tag );
                table.border = &#39;0&#39;;
                document.getElementsByClassName( to )[ njson - 1 ].appendChild( table );
                table.classList.add( class_name );
                ++ntable;
                break;

                case &#39;TR&#39;:
                var tr = document.createElement( tag );
                document.getElementsByClassName( to )[ ntable - 1 ].appendChild( tr );
                tr.classList.add( class_name );
                ++ntr;
                break;

                case &#39;TH&#39;:
                var th = document.createElement( tag );
                document.getElementsByClassName( to )[ ntr - 1 ].appendChild( th );

                var contents = document.createTextNode( text );
                th.appendChild( contents );

                th.classList.add( class_name );
                ++nth;
                break;

                case &#39;TD&#39;:
                var table = document.getElementsByClassName( &#39;table&#39; )[ ntable - 1 ];
                table.border = &#39;1&#39;;
                var td = document.createElement( tag );

                var contents = document.createTextNode( text );
                td.appendChild( contents );

                document.getElementsByClassName( to )[ ntr - 1 ].appendChild( td );
                td.classList.add( class_name );
                ++ntd;
                break;
            }
        }
    }

