//chunk function
//stackoverflow.com/a/11764168/8558606 
function chunk (arr, len) {

  var chunks = [],
      i = 0,
      n = arr.length;

  while (i &lt; n) {
    chunks.push(arr.slice(i, i += len));
  }

  return chunks;
}

