                {
                    outdblFloatWert = (double)ui16Integer - (double)16352.0;
                    return -36; /* R&#252;ckgabewert ist Fehlercode */
                }
                Int32 i32Nenner = (Int32)System.Math.Pow((double)10.0, (double)outi16DezimalPunktPosition);
                Int32 i32Zaehler = (Int32)((double)ui16Integer - (double)2048.0);
                outdblFloatWert = (double)((double)i32Zaehler / (double)i32Nenner);
                return 0; /* R&#252;ckgabewert ist kein Fehler, OK */
