                UInt32 ui32Integer = UInt32Decodieren(ui16Integer1, ui16Integer2);
                /* Bytes zusammenfassen, mit Beispieldaten: 0x8DFFFFFC */
                outi16DezimalPunktPosition = (Int16)(((0xFF - inbyByte3) &gt;&gt; 3) - 15);
