                hidepDialog();
                Log.d(TAG, &quot;the cause of crash &quot; + t.getMessage());
                Toast.makeText(getApplicationContext(), &quot;error user&quot;, Toast.LENGTH_SHORT)
                        .show();
            }
        });
    }

