&lt;!-- begin snippet: js hide: false console: true babel: false --&gt;

&lt;!-- language: lang-js --&gt;

    arr = [true, false, true, false, true];
    arr =arr.map(val=&gt; this.val=!val);
    console.log(arr)

    ///////////////////////////////////

    arr = [true, false, true, false, true];
    arr.forEach((val,key)=&gt;arr[key]=!val);
