    class PriorityQueue {

    PriorityQueue array[] = new PriorityQueue[10];
    private int front;
    private int rear;
    private int count;
    private int eventType;
    double eventTime;
    private double tallyTime;
    private boolean paymentMethod;

    public PriorityQueue(int evT, int eT, double tT, boolean pM) {
        this.eventType = evT;
        this.eventTime = eT;
        this.tallyTime = tT;
        this.paymentMethod = pM;
        front = 0;
        rear = -1;
        count = 0;
    }

    public void enqueue(PriorityQueue pQ) {
        if (isFull()) {
            System.out.println(&quot;OverFlow\nProgram Terminated&quot;);
            System.exit(1);
        }

        rear = (rear + 1);
        array[rear] = pQ;
        count++;
    }

    public void sort(PriorityQueue arr[]) {
        int n = arr.length;

        // Build heap (rearrange array) 
        for (int i = n / 2 - 1; i &gt;= 0; i--) {
            heapify(arr, n, i);
        }

        // One by one extract an element from heap 
        for (int i = n - 1; i &gt;= 0; i--) {
            // Move current root to end 
            int temp = (int) arr[0].eventTime;
            arr[0] = arr[i];
            arr[i].eventTime = temp;

            // call max heapify on the reduced heap 
            heapify(arr, i, 0);
        }
    }

    void heapify(PriorityQueue arr[], int n, int i) {
        int largest = i; // Initialize largest as root 
        int l = 2 * i + 1; // left = 2*i + 1 
        int r = 2 * i + 2; // right = 2*i + 2 

        // If left child is larger than root 
        if (l &lt; n &amp;&amp; arr[l].eventTime &gt; arr[largest].eventTime) {
            largest = l;
        }

        // If right child is larger than largest so far 
        if (r &lt; n &amp;&amp; arr[r].eventTime &gt; arr[largest].eventTime) {
            largest = r;
        }

        // If largest is not root 
        if (largest != i) {
            int swap = (int) arr[i].eventTime;
            arr[i] = arr[largest];
            arr[largest].eventTime = swap;

            // Recursively heapify the affected sub-tree 
            heapify(arr, n, largest);
        }
    }

    public void dequeue() {
        if (isEmpty()) {
            System.out.println(&quot;UnderFlow\nProgram Terminated&quot;);
            System.exit(1);
        }

        front = (front + 1) % array.length;
        count--;
    }

    public boolean isEmpty() {

        return (size() == 0);
    }

    public int size() {
        return count;
    }

    public boolean isFull() {
        return (size() == array.length);
    }

    public PriorityQueue peek() {
        if (isEmpty()) {
            System.out.println(&quot;UnderFlow\nProgram Terminated&quot;);
            System.exit(1);
        }
        return array[front];
    }

