        public class MyApp
        {
           private readonly IDataProvider _dbProvider;
           private readonly IHelper _h;
           public MyApp(IDataProvider dbProvider)
           {
               _dbProvider = dbProvider;
               _h = new Helper(_dbProvider);
           }
        
