    class Random {
      constructor( seed ) {
        this._seed = seed % 0x7fffffff;
        if (this._seed &lt;= 0) { this._seed += 0x7ffffffe; }
      }
      next() {
        const next_int = this._seed =
          this._seed * 0x41a7 % 0x7fffffff;

        return ( next_int - 1 ) / 0x80000000;
      }
    }

