&lt;!-- language: lang-html --&gt;

    &lt;div
      ng-app=&quot;app&quot;
      ng-controller=&quot;ctrl as $ctrl&quot;&gt;
      &lt;select
        ng-model=&quot;$ctrl.vals&quot;
        ng-options=&quot;$ctrl.modelFor(obj) as $ctrl.labelFor(obj) for obj in $ctrl.data&quot;&gt;&lt;/select&gt;
