      transition: all 0.5s ease;
    }

    .item img:hover,
    .item img:active {
      transform: scale(1.25);
      margin-bottom: 30px;
    }


&lt;!-- language: lang-html --&gt;

    &lt;div class=&quot;container item&quot;&gt;
      &lt;a href=&quot;#&quot;&gt;&lt;img src=&quot;http://lorempixel.com/output/nature-q-c-300-300-8.jpg&quot;&gt;&lt;/a&gt;
