    			&lt;/xsl:call-template&gt;
    		&lt;/xsl:variable&gt;
    		&lt;xsl:variable name=&quot;varWhse&quot;&gt;           
    			&lt;xsl:value-of select=&quot;substring($headerDuns, 11, 3)&quot; /&gt;
