    		&lt;xsl:for-each select=&quot;current()&quot;&gt;				
    			&lt;transaction varwhse=&quot;{$varWhse}&quot;&gt;				
    				&lt;duns number=&quot;{$headerDuns}&quot; /&gt;
    			&lt;/transaction&gt;
    		&lt;/xsl:for-each&gt;
    	&lt;/xsl:template&gt;
    &lt;/xsl:stylesheet&gt;

