    def multiformat(string, format_list, format_res):
        delim = None
        if &#39;_&#39; in string:
            delim = &#39;_&#39;
        elif &#39;-&#39; in string:
            delim = &#39;-&#39;
        else:
            delim = &#39; &#39;
    
        strdate = string.split(delim)[1].strip().split(&#39;.&#39;)[:-1]
        txtdate = &#39; &#39;.join(strdate)
    
