        temp += &#39;&lt;div&gt;&#39; + (&#39;-&#39;.repeat(dash)) + &#39; &#39; + k + &#39;&lt;/div&gt;&#39;;
        
        if (typeof obj[k] == &quot;object&quot; &amp;&amp; obj[k] !== null) {
          dash1 = dash + 1;
          temp += eachRecursive(obj[k], text, dash1);
        }
      }

      return text + temp
    }

