  if (sum(nas) == length(x)) {
    warning(&#39;all elements were NA&#39;)
    return(c(start_na = NA_integer_, end_na = NA_integer_))
  }
  
  c(start_na = which.min(nas) - 1,
    end_na = which.min(rev(nas)) - 1)
