    return pair(f).first;
}

template&lt;typename T1, typename T2&gt;
T2 cdr(Pair&lt;T1, T2&gt; pair) {
    F&lt;T1, T2&gt; f = std::make_pair&lt;T1, T2&gt;;
    return pair(f).second;
}

template&lt;typename T1, typename T2&gt;
Pair&lt;T1, T2&gt; cons(T1 first, T2 second) {
    Pair&lt;T1, T2&gt; pair = [first, second](F&lt;T1, T2&gt; f) {
        return f(first, second);
    };
    return pair;
}

