use std::ffi::OsStr;

fn main() {
    let path = OsStr::from_bytes(b&quot;./foo/bar\xff.txt&quot;);
    let path = Path::new(path);
    
    println!(&quot;{}&quot;, path.display()); // ./foo/bar�.txt
    println!(&quot;{:?}&quot;, path); // &quot;./foo/bar\xFF.txt&quot;
}
```

