    $tag = &quot;~\[(?P&lt;tag&gt;\w+)[^][]*\]~&quot;;
    $key_values = &#39;~(?P&lt;key&gt;\w+)=&quot;(?P&lt;value&gt;[^&quot;]*)&quot;~&#39;;
    
    preg_match_all($tag, $junk, $matches, PREG_SET_ORDER);
