```
function delegate(el, evt, sel, handler) {
    const listener = function(event) {
        var t = event.target;
        while (t &amp;&amp; t !== this) {
            if (t.matches(sel)) {
                handler.call(t, event);
            }
            t = t.parentNode;
        }
    };
    el.addEventListener(evt, listener);
    return listener;
}

