    	EXECUTE IMMEDIATE sstmt INTO res USING XMLTYPE(&#39;&lt;froot xmlns=&quot;http://www.ff.org&quot;&gt;&lt;/froot&gt;&#39;);
    	DBMS_OUTPUT.PUT_LINE ( &#39;res = &#39; || res );
    
    
    	sstmt  := q&#39;# 
    	SELECT 
