    		ELSE 2 END       
    	FROM 
    		XMLTABLE(
    			XMLNAMESPACES(DEFAULT &#39;#&#39;||sNamespace||&#39;&#39;&#39;),&#39;||
    	q&#39;# 
    			&#39;$d&#39; PASSING XMLTYPE(:x) as &quot;d&quot; COLUMNS 
