```
/**
 * Reducer function for all async use... functions herein.
 */
const dataFetchReducer = (state, action) =&gt; {
  switch (action.type) {
    case &#39;FETCH_INIT&#39;:
      return { 
        ...state, 
        isLoading: true, 
        isError: false 
      };
    case &#39;FETCH_SUCCESS&#39;:
      return {
        ...state,
        isLoading: false,
        isError: false,
        data: action.payload,
      };
    case &#39;FETCH_FAILURE&#39;:
      return {
        ...state,
        isLoading: false,
        isError: true,
        data: null
      };
    default:
      throw new Error();
  }
}

/**
 * GET data from endpoint using AWS Access Token
 * @param {string} initialUrl   The full path of the endpoint to query
 * @param {JSON}   initialData  Used to initially populate &#39;data&#39;
 */
export const useFetch = (initialUrl:string, initialData:any) =&gt; {
  const [url, setUrl] = useState&lt;string&gt;(initialUrl);
  const { appStore } = useContext(AppContext);

  const [state, dispatch] = useReducer(dataFetchReducer, {
    isLoading: false,
    isError: false,
    data: initialData
  });

  useEffect(() =&gt; {
    let didCancel = false;
    const options = appStore.awsConfig;

    const fetchData = async () =&gt; {
      dispatch({ type: &#39;FETCH_INIT&#39; });

      try {
        let response;
        if (url &amp;&amp; options) {
          response = await axios.get(url, options);
        }
        
        if (!didCancel) {
          dispatch({ type: &#39;FETCH_SUCCESS&#39;, payload: response.data });
        }
      } catch (error) {
        // We won&#39;t force an error if there&#39;s no URL
        if (!didCancel &amp;&amp; url !== null) {
          dispatch({ type: &#39;FETCH_FAILURE&#39; });
        }
      }
    };

    fetchData();

    return () =&gt; {
      didCancel = true;
    };
  }, [url, appStore.awsConfig]);

  return [state, setUrl];
}
```

