						dummy ^= d.ContainsKey (i);
					}
					wsl.Stop ();
				}

				string perM (Stopwatch w) =&gt; $&quot;{w.Elapsed.TotalSeconds / n * 10 * 1000 * 1000,5:0.00}&quot;;
				Console.WriteLine ($&quot;init = {init,-5}, n = {n,8};&quot;
					+ $&quot; insert = {perM (wmi)}/{perM (wsi)},&quot;
					+ $&quot; lookup = {perM (wml)}/{perM (wsl)}&quot;);
			}
		}
		Console.WriteLine ();
	}

