    var id = UUID()
    var a: String
    var b: String
    var c: String
    var isActive: Bool
}

struct ContentView: View {
    @State private var modelArray = [
        Model(a:&quot;hi&quot;, b:&quot;I like&quot;, c: &quot;potatoes&quot;, isActive: true),
        Model(a:&quot;hi&quot;, b:&quot;I like&quot;, c: &quot;potatoes&quot;, isActive: false),
