    const rotatePoint = (x, y, centerx, centery, degrees)=&gt;{ // https://stackoverflow.com/a/45649110/2244284
    	let newx = (x - centerx) * Math.cos(degrees * Math.PI / 180) - (y - centery) * Math.sin(degrees * Math.PI / 180) + centerx;
