&lt;!-- language: lang-css --&gt;

    .row {
      background: #f8f9fa;
      margin-top: 5px;
    }

    .col {
      border: solid 1px #6c757d;
      padding: 10px;
    }

    .myScale1:hover {
      transform: scale(1.05);
      position:relative;
      z-index:1;
    }

