    if(filedata$notPresent){
      
      filePresent &lt;- list.files(&quot;www/srcdata/&quot;, pattern = paste0(&quot;data_&quot;,Sys.Date()))

      if(length(filePresent)==1){
        data &lt;- readRDS(paste0(&quot;www/srcdata/data_&quot;,Sys.Date()))
