    const appendComment = (state = {}, parentId = 0, c = {}) =&gt;
    { for (const path of searchById(state, parentId))
        return updateIn
          ( fromJS (state)
          , [ ...path, &#39;comments&#39; ]
          , list =&gt; list .push (c)
          )
          .toJS ()
      return state
    }

    const state =
      { posts:
          [ { id: 1
            , topic: &#39;Topic A&#39;
            , comments: []
            }
          , { id: 2
            , topic: &#39;Topic B&#39;
            , comments: []
