        }
      },
        login: function() {
          this.axios
            .post(&quot;&quot;, { user: this.username, password: this.password })
            .then(request =&gt; this.loginSuccessful(request))
            .catch(() =&gt; this.loginFailed());
        },

        loginSuccessful(req) {
          if (!req.data.token) {
            this.loginFailed();
            return;
          } 
          localStorage.token = req.data.token;
          this.error = false;

          this.$router.replace(this.$route.query.redirect || &quot;/dashboard&quot;);
