        keep = [{ id: &quot;43&quot;, text: &quot;B [43]&quot; }, { id: &quot;93&quot;, text: &quot;B [93]&quot; }, { id: &quot;66&quot;, text: &quot;B [66]&quot; }, { id: &quot;13&quot;, text: &quot;I [13]&quot; }],
