    =&gt; {
           :posts =&gt; [
            [0] {
                   :id =&gt; 3,
                :title =&gt; &quot;Post 3&quot;
            }
        ],
         :profile =&gt; {
            :name =&gt; &quot;&quot;
        },
        :nulldata =&gt; {
            :res =&gt; nil
        }
    }

