        [[&#39;a&#39;, 3], [&#39;b&#39;, 16], [&#39;c&#39;, 4]],
        [[&#39;c&#39;, 6], [&#39;b&#39;, 4], [&#39;a&#39;, 5], [&#39;b&#39;, 5]]
    ]));
    console.log(countAbc([
        [[&#39;a&#39;, 900000], [&#39;b&#39;, 100000]],
        [[&#39;a&#39;, 750000], [&#39;b&#39;, 250000]],
        [[&#39;a&#39;, 250000], [&#39;c&#39;, 750000]], 
