    awk &#39;
    BEGIN{
      s1=&quot; &quot;
      FS=OFS=&quot;|&quot;
      re=&quot;(.*)\\|\\|\\|(.*)&quot;
    }
    prev{
      print gensub(re,&quot;\\1|\\2&quot;,&quot;1&quot;,$0)
    }
    {
      $2=$2 s1 $3 s1 $4
      $3=$4=&quot;&quot;
      prev=$0
