    let userPref_1 = 1, 
        userPref_2 = 0,
        userPref_3 = 1;

    // Restore last selected user settings.

    document.querySelector(&#39;.set_1&#39;).checked = userPref_1 === 1 ? true : false;
    document.querySelector(&#39;.set_2&#39;).checked = userPref_2 === 1 ? true : false;
