function read (fileName) {
    return new Promise((resolve, reject) =&gt; {
        fs.readFile(fileName, &#39;utf8&#39;,
            function (err, textFileData) {
                if (err) {
                    reject(err);
                    return;
                }

                resolve(textFileData);
            }
        );
    });
};

