            foreach($subset AS $bit) $bits |= 1 &lt;&lt; $bit;

            # Find the lowest cost to get to this subset
            foreach($subset AS $bk) {
                $prev = $bits &amp; ~(1 &lt;&lt; $bk);

                $res = [];
                foreach($subset AS $m) {
