        // pre-C++11
        X::foo X::f()      { /* ... */ }
        
        // trailing, doesn&#39;t require `X::` before `foo`
        auto X::f() -&gt; foo { /* ... */ }

 2. Returning a complicated type, such as a function pointer type:

        // pre-C++11
        int(*g(float))(int) { /* ... */ }

        // trailing, easier to read
        auto f(float) -&gt; int(*)(int) { /* ... */ }

