def merge(list1, list2): 
    if list1 == [] or list2 == []:
        return []
    merged_list = [] 
    for i in range(max((len(list1), len(list2)))): 
        while True: 
            try: 
                tup = (list1[i], list2[i]) 
            except IndexError: 
                if len(list1) &gt; len(list2): 
                    list2.append(list2[-1])
                    tup = (list1[i], list2[i]) 
                elif len(list1) &lt; len(list2): 
                    list1.append(list1[-1]) 
                    tup = (list1[i], list2[i]) 
                continue
