&lt;!-- language: lang-js --&gt;

    var value = $(&#39;#value&#39;);
    $(value).change(function() {
      debugger;
      if (Number($(this).val()) &gt; 60) {
        $(&#39;#result&#39;).val(&#39;Long&#39;); // 1. Setting the value correctly with jQuery
