        function calcLine(p0, p1, angle, sign) {
            let w = +thik.value,
                pi2 = Math.PI / 2;
            return [
                shift(p0, angle + pi2 * sign, w),
                shift(p1, angle + pi2 * sign, w)
            ]
        }

        function calcSegment(p0, p1, sign) {
            let x = p0.x - p1.x,
                y = p0.y - p1.y,
                angle = Math.atan2(y, x),
                bottom = calcLine(p0, p1, angle, sign),
                top = calcLine(p0, p1, angle, -sign);
            return {
                bottom,
                polygon: [top[1], top[0], ...bottom]
            }
