        function pointInPolygon(point, polygon) {
            var x = point.x,y = point.y,inside;
            for (var i = 0, j = polygon.length - 1; i &lt; polygon.length; j = i++) {
                let xi = polygon[i].x,yi = polygon[i].y;
                let xj = polygon[j].x,yj = polygon[j].y;
                let intersect = yi &gt; y != yj &gt; y &amp;&amp;
                    x &lt; (xj - xi) * (y - yi) / (yj - yi) + xi;
                if (intersect) inside = !inside;
            }
            return inside;
        }

        function polygonLineIntersection(pts, line) {
            for (let i = 0; i &lt; pts.length; i++) {
                let j = i ? i : pts.length;
                let p = linesIntersection([pts[j - 1], pts[i]], line);
                if (p) return p
            }
        }

        function linesIntersection(line1, line2, extrapolate) {
            let p1 = line1[0], 
                p2 = line1[1], 
                p3 = line2[0], 
                p4 = line2[1];
            let dx1 = p2.x - p1.x, 
                dy1 = p2.y - p1.y;
            let dx2 = p4.x - p3.x, 
                dy2 = p4.y - p3.y; 
            let denom = dy2 * dx1 - dx2 * dy1;
            if (denom == 0) return null;
            let dx = p1.x - p3.x, 
                dy = p1.y - p3.y;
            let ua = (dx2 * dy - dy2 * dx) / denom;
            let ub = (dx1 * dy - dy1 * dx) / denom;
            let intersect = ua &gt;= 0 &amp;&amp; ua &lt;= 1 &amp;&amp; ub &gt;= 0 &amp;&amp; ub &lt;= 1;
